import fs from"fs/promises";import path from"path";export const getIconsByCategory=async()=>{const baseDir=path.join(process.cwd(),"public/icons"),categories=await fs.readdir(baseDir),result={};for(const category of categories){const categoryPath=path.join(baseDir,category);if((await fs.stat(categoryPath)).isDirectory()){const svgFiles=(await fs.readdir(categoryPath)).filter((f=>f.endsWith(".svg")));result[category]=svgFiles.map((file=>`${category}/${file}`))}}return result};